import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Route} from 'react-router';
import { BrowserRouter as Router } from 'react-router-dom'

import teams from './teams';
import members from './members';
import {fetchList} from "./data-utils";
import {Button} from "react-bootstrap";

class MainContainer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            isTeamPage: true,
            teams: [],
            members: [],
        };
        this.buttonListChangeClick = this.buttonListChangeClick.bind(this);
    }

    buttonListChangeClick = () => {
        this.setState({isTeamPage: !this.state.isTeamPage}, () => {
            fetchList(this.state.isTeamPage, (list, status) => {
                if (this.state.isTeamPage) {
                    this.setState({
                        teams: list,
                        isLoaded: status.isLoaded,
                        error: status.error
                    });
                } else {
                    this.setState({
                        members: list,
                        isLoaded: status.isLoaded,
                        error: status.error
                    });
                }
            });
        });
    }


    componentDidMount() {
        fetchList(this.state.isTeamPage, (list, status) => {
            if (this.state.isTeamPage) {
                this.setState({
                    teams: list,
                    isLoaded: status.isLoaded,
                    error: status.error
                });
            } else {
                this.setState({
                    members: list,
                    isLoaded: status.isLoaded,
                    error: status.error
                });
            }
        });
    }

    render() {
        const {error, isLoaded} = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <div className="container marketing team-container">
                    <section className="jumbotron text-center">
                        <div className="container">
                            <h1 className="jumbotron-heading">FrontEnd Test Demo</h1>
                            <p>
                                <Button
                                    className={"btn" + this.state.isTeamPage ? 'btn-primary' : 'btn-secondary' + "my"}
                                    onClick={this.buttonListChangeClick}>{this.state.isTeamPage ? "Teams" : "Members"}</Button>
                            </p>
                        </div>

                    </section>
                    <Router>
                        <div>
                            <Route path="/teams" exact component={teams} />
                            <Route path="/members" exact component={members} />
                            <Route path="*" component={teams} />
                        </div>

                    </Router>
                </div>

            );
        }
    }
}

ReactDOM.render(
    <MainContainer />,

    document.getElementById('root')
);
