import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Row, Grid, Button, Modal} from 'react-bootstrap';
import {fetchMember, fetchTeam, fetchList} from './data-utils'

export default class Teams extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            showModal: false,
            team: {members: []},
            member: {teams: []},
            teams: [],
            members: []
        };
        this.showModalForDetails = this.showModalForDetails.bind(this);
        this.dissmissModalForDetails = this.dissmissModalForDetails.bind(this);
    }

    showModalForDetails(ev, id) {
        this.setState({
            showModal: true,
            teams: [],
            members: []
        });
        if (this.props.isTeamPage) {
            fetchTeam(id, (team, status) => {
                this.setState({
                    team: team,
                    isLoaded: status.isLoaded,
                    error: status.error
                });
                let arr = [];
                team.members.map(
                    (memberId) => {
                        fetchMember(memberId, (member, status) => {
                            arr = this.state.members;
                            arr.push(member);
                            this.setState({
                                members: arr
                            })
                        })
                    }
                );
            });
        } else {
            fetchMember(id, (member, status) => {
                this.setState({
                    member: member,
                    isLoaded: status.isLoaded,
                    error: status.error
                });
                let arr = [];
                member.member_teams.map(
                    (teamId) => {
                        fetchTeam(teamId, (team, status) => {
                            arr = this.state.teams;
                            arr.push(team);
                            this.setState({
                                teams: arr
                            })
                        })
                    }
                );
            });
        }
    }

    dissmissModalForDetails() {
        this.setState({
            showModal: false,
            teams: [],
            members: []
        });
    }

    render() {
        if (this.props.isTeamPage) {
            return (
                <Grid className="container marketing">
                    <DetailsModal showModal={this.state.showModal} dissmissModal={this.dissmissModalForDetails}
                                  details={this.state.team} members={this.state.members}
                                  isTeamPage={this.props.isTeamPage}/>
                    <Row>
                        {
                            this.props.teams.map((team) => (
                                <div className="col-lg-4" key={team.id}>
                                    <img className="rounded-circle"
                                         src="data:image/gif;base64,R0lGODlhAQABAIAAAHd3dwAAACH5BAAAAAAALAAAAAABAAEAAAICRAEAOw=="
                                         alt="Team Image" width="140" height="140"/>
                                    <h2>{team.name}</h2>
                                    <TeamLeader teamId={team.id}/>
                                    <p><Button className="btn btn-secondary" onClick={(ev) => {
                                        this.showModalForDetails(ev, team.id)
                                    }}>Show Detail »</Button></p>
                                </div>
                            ))}
                    </Row>
                </Grid>);
        } else {
            return (
                <Grid className="container marketing">
                    <DetailsModal showModal={this.state.showModal} dissmissModal={this.dissmissModalForDetails}
                                  details={this.state.member} teams={this.state.teams}
                                  isTeamPage={this.props.isTeamPage}/>
                    <Row>
                        {
                            this.props.members.map((member) => (
                                <div className="col-lg-4" key={member.id}>
                                    <img className="rounded-circle"
                                         src="data:image/gif;base64,R0lGODlhAQABAIAAAHd3dwAAACH5BAAAAAAALAAAAAABAAEAAAICRAEAOw=="
                                         alt="Team Image" width="140" height="140"/>
                                    <h2>{member.username}</h2>
                                    <p>Name: {member.name}</p>
                                    <p><a className="btn btn-secondary" href="#" role="button" onClick={(ev) => {
                                        this.showModalForDetails(ev, member.id)
                                    }}>Show Details »</a></p>
                                </div>
                            ))}
                    </Row>
                </Grid>);
        }
    }
}

class TeamLeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            team: {},
            member: {}
        };
    }

    componentDidMount() {
        fetchTeam(this.props.teamId, (team, status) => {
            fetchMember(team.lead, (member, status) => {
                this.setState(
                    {
                        member: member,
                        isLoaded: status.isLoaded,
                        error: status.error
                    }
                );
            });
        });
    }

    render() {
        const {error, isLoaded, member} = this.state;

        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <p>TeamLeader: {member.name}</p>
            );
        }
    }
}

class DetailsModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {

        if (this.props.isTeamPage) {
            return (
                <div className={this.props.showModal ? 'display-block' : 'display-none'}>
                    <Modal.Dialog>
                        <Modal.Header>
                            <Modal.Title>{this.props.details.name}</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <ul>
                                <li><h4>Leader: {this.props.details.lead}</h4></li>
                                <h6>Members:</h6>
                                <ErrorBoundary>
                                    <MembersForTeamDetails members={this.props.members}/>
                                </ErrorBoundary>
                            </ul>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button bsStyle="primary" onClick={this.props.dissmissModal}>OK</Button>
                        </Modal.Footer>
                    </Modal.Dialog>
                </div>
            );
        } else {

            return (
                <div className={this.props.showModal ? 'display-block' : 'display-none'}>
                    <Modal.Dialog>
                        <Modal.Header>
                            <Modal.Title>{this.props.details.username}</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <ul>
                                <h3>Name: {this.props.details.name}</h3>
                                <h6>Teams:</h6>
                                <ErrorBoundary>
                                    <TeamsForMemberDetails teams={this.props.teams}/>
                                </ErrorBoundary>
                            </ul>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button bsStyle="primary" onClick={this.props.dissmissModal}>OK</Button>
                        </Modal.Footer>
                    </Modal.Dialog>
                </div>
            );
        }
    }
}

