import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Row, Grid, Button, Modal} from 'react-bootstrap';
import {fetchMember, fetchTeam, fetchList} from './data-utils'

export default class Teams extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            showModal: false,
            team: {members: []},
            member: {teams: []},
            teams: [],
            members: []
        };
        this.showModalForDetails = this.showModalForDetails.bind(this);
        this.dissmissModalForDetails = this.dissmissModalForDetails.bind(this);
    }

    showModalForDetails(ev, id) {
        this.setState({
            showModal: true,
            teams: [],
            members: []
        });
        fetchTeam(id, (team, status) => {
            this.setState({
                team: team,
                isLoaded: status.isLoaded,
                error: status.error
            });
            let arr = [];
            team.members.map(
                (memberId) => {
                    fetchMember(memberId, (member, status) => {
                        arr = this.state.members;
                        arr.push(member);
                        this.setState({
                            members: arr
                        })
                    })
                }
            );
        });
    }

    dissmissModalForDetails() {
        this.setState({
            showModal: false,
            teams: [],
        });
    }

    componentDidMount() {
        fetchList(true, (teams, status) => {
            if (status.isLoaded && status.error == null) {
                let arr = [];
                teams.map(
                    (team) => {
                        fetchMember(team.lead, (leader, status) => {
                            team.lead = leader;
                            arr.push(team);
                            this.setState(
                                {
                                    teams: arr
                                }
                            );
                        })
                    }
                );
            }
            this.setState(
                {
                    isLoaded: status.isLoaded,
                    error: status.error
                }
            );
        })
    }

    render() {
        return (
            <Grid className="container marketing">
                <DetailsModal showModal={this.state.showModal} dissmissModal={this.dissmissModalForDetails}
                              details={this.state.team} members={this.state.members}
                              isTeamPage={this.props.isTeamPage}/>
                <Row>
                    {
                        this.state.teams.map((team) => (
                            <div className="col-lg-4" key={team.id}>
                                <img className="rounded-circle"
                                     src="data:image/gif;base64,R0lGODlhAQABAIAAAHd3dwAAACH5BAAAAAAALAAAAAABAAEAAAICRAEAOw=="
                                     alt="Team Image" width="140" height="140"/>
                                <h2>{team.name}</h2>
                                <p>{team.lead}</p>
                                <p><Button className="btn btn-secondary" onClick={(ev) => {
                                    this.showModalForDetails(ev, team.id)
                                }}>Show Detail Â»</Button></p>
                            </div>
                        ))}
                </Row>
            </Grid>);
    }

}

class DetailsModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {

        if (this.props.isTeamPage) {
            return (
                <div className={this.props.showModal ? 'display-block' : 'display-none'}>
                    <Modal.Dialog>
                        <Modal.Header>
                            <Modal.Title>{this.props.details.name}</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <ul>
                                <li><h4>Leader: {this.props.details.lead}</h4></li>
                                <h6>Members:</h6>
                                <ErrorBoundary>
                                    <MembersForTeamDetails members={this.props.members}/>
                                </ErrorBoundary>
                            </ul>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button bsStyle="primary" onClick={this.props.dissmissModal}>OK</Button>
                        </Modal.Footer>
                    </Modal.Dialog>
                </div>
            );
        } else {

            return (
                <div className={this.props.showModal ? 'display-block' : 'display-none'}>
                    <Modal.Dialog>
                        <Modal.Header>
                            <Modal.Title>{this.props.details.username}</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <ul>
                                <h3>Name: {this.props.details.name}</h3>
                                <h6>Teams:</h6>
                                <ErrorBoundary>
                                    <TeamsForMemberDetails teams={this.props.teams}/>
                                </ErrorBoundary>
                            </ul>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button bsStyle="primary" onClick={this.props.dissmissModal}>OK</Button>
                        </Modal.Footer>
                    </Modal.Dialog>
                </div>
            );
        }
    }
}

class TeamsForMemberDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            list: []
        };
    }

    render() {
        return (
            <div>
                {this.props.teams.map(
                    (team) => (
                        <p key={team.id}>{team.name}</p>
                    )
                )}
            </div>
        );
    }
}

class MembersForTeamDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            list: []
        };
    }

    render() {
        return (
            <div>
                {this.props.members.map(
                    (member) => (
                        <p key={member.id}>{member.name}</p>
                    )
                )}
            </div>
        );
    }
}

class ErrorBoundary extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, errorInfo: null};
    }

    componentDidCatch(error, errorInfo) {
        // Catch errors in any components below and re-render with error message
        this.setState({
            error: error,
            errorInfo: errorInfo
        })
        // You can also log error messages to an error reporting service here
    }

    render() {
        if (this.state.errorInfo) {
            // Error path
            return (
                <div>
                    <h2>Something went wrong.</h2>
                    <details style={{whiteSpace: 'pre-wrap'}}>
                        {this.state.error && this.state.error.toString()}
                        <br/>
                        {this.state.errorInfo.componentStack}
                    </details>
                </div>
            );
        }
        // Normally, just render children
        return this.props.children;
    }
}
